package net.miwashi.stijdbc;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.sql.*;

public class App {
    private static final Logger LOGGER = LoggerFactory.getLogger( App.class );

    public static void main(String[] args) {
        System.out.println("Running");
        System.out.println("Running");
        System.out.println("Running");
        Connection con = null;
        try {
            Class.forName("com.mysql.cj.jdbc.Driver");
            con = DriverManager.getConnection("jdbc:mysql://localhost:3306/stidb", "sti", "sti");
            ResultSet rs = con.createStatement().executeQuery("SELECT * FROM FinalStudent_Hobby");
            while(rs.next()) {
                System.out.print(rs.getString(1));
                System.out.print(" - ");
                System.out.print(rs.getString(2));
                System.out.println();
            }
        } catch (ClassNotFoundException e) {
            LOGGER.warn("Driver for mysql is not in classpath!", e);
        } catch (SQLException e) {
            LOGGER.warn("Exception caught when connection to database!", e);
        } finally {
            if(con!=null) {
                try {
                    con.close();
                } catch (SQLException ignore) {
                    LOGGER.warn("Unable to close connection!", ignore);
                }
            }
        }
    }
}
